import React from 'react';
import { SafeAreaView, View, FlatList,Image, StyleSheet, Text, StatusBar } from 'react-native';
import axios from 'axios';

export default class List extends React.Component {
    state = {
        recette: []
      }

      componentDidMount() {
        // axios.get(`https://api.spoonacular.com/recipes/complexSearch?apiKey=bbf788cda4f34fcc8b58403847622e97&number=100`)
        //   .then(res => {
        //     const recette = res.data.results;
        //     this.setState({ recette });
        //   })
        fetch("data.json").then(resp=>resp.json()).then(console.log(resp));
      }

      render() {
          console.log(this.state.recette);
        const recipe = this.state.recette
        const renderItem = ({ item }) => (
            <View>
                    <li>
                        <Image source={{uri: item.image}}/>
                        <Text>{item.title}</Text>
                    </li>
            </View>
          );

        return (
            <FlatList
                data={recipe}
                renderItem={renderItem}
                keyExtractor={item => item.id}
            />
        )
      }
}
