import React from 'react';
import { StyleSheet, Text, View,Button } from 'react-native';

class DetailRecipe extends React.Component {
  render() {
    return (
      <View style={styles.container}>
        <Text>{JSON.stringify(this.props.route.params.item.title)}</Text>
        <Text>{JSON.stringify(this.props.route.params.item.id)}</Text>
        <Image source={{uri: this.props.route.params.item.image}} style={styles.recipeImage}></Image>

      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  recipeImage: {
    width:350,
    height :90,
    flex: 1,
    alignSelf: 'center',
    textAlign: 'center',
    padding :15,
    margin : 'auto',
    marginTop : 20
}
});

export default DetailRecipe;
