import React from 'react';
import { StyleSheet, Text, View,Button,Image } from 'react-native';
import axios from 'axios';


class DetailRecipe extends React.Component {
  state = {
    recette: []
  }

  componentDidMount() {
    axios.get(`https://api.spoonacular.com/recipes/${this.props.route.params.item.id}/information?apiKey=bbf788cda4f34fcc8b58403847622e97`)
      .then(res => {
        const recette = res.data;
        this.setState({ recette });
        console.log(recette);
      })
      console.log(`https://api.spoonacular.com/recipes/${this.props.route.params.item.id}/information?apiKey=bbf788cda4f34fcc8b58403847622e97`)
  }
  render() {
    return (
      <View style={styles.container}>
        <View style={styles.colLeft}>
          <Image source={{uri: this.props.route.params.item.image}} style={styles.recipeImage}/>
        </View>
        <View style={styles.colRight}>
          <Text>{JSON.stringify(this.props.route.params.item.title)}</Text>
          <Text>{JSON.stringify(this.props.route.params.item.id)}</Text>
          <Text>{this.state.recette.creditsText}</Text>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  recipeImage: {
    width:500,
    flex: 1,
    alignSelf: 'center',
    textAlign: 'center',
    padding :15,
    margin : 'auto',
    marginTop : 20
}
});

export default DetailRecipe;
