import React from 'react';

import axios from 'axios';

export default class PersonList extends React.Component {
    state = {
        recette: {}
      }

      componentDidMount() {
        axios.get(`https://api.spoonacular.com/recipes/716429/information?apiKey=bbf788cda4f34fcc8b58403847622e97&includeNutrition=false`)
          .then(res => {
            const recette = res.data;
            console.log(res.data),
            this.setState({ recette });
          })
      }

      render() {
          const recipe = this.state.recette
        return (
          <ul>
            { JSON.stringify(recipe)}
            {recipe.image}
          </ul>
        )
      }
}
